sys_script_include.script:
var admintools_ajaxinterfaces = Class.create();
admintools_ajaxinterfaces.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    ajax_getEmptyColumnsForTable: function () {
        var tableName = this.getParameter('sysparm_tablename');
        if (tableName == "")
            return new Error("Table Name must be provided");

        try {
            var admintools = new AdminTools_TableInspector(tableName);
            var results = admintools.getEmptyColumns();
            var dataForTable = [];
            results.forEach(function(item){
                dataForTable.push([item]); // Yes this is weird, but DataTables expects each row in its own array, which makes sense because multiple fields
            })
            return JSON.stringify({
                "error": false,
                "data": dataForTable,
                "filename": "ServiceNow Table '" + tableName + "' empty column report"
            });
        } catch (ex) {
            gs.debug(ex);
            return JSON.stringify({
                "error": true
            });
        }
    },

    type: 'admintools_ajaxinterfaces'
});
