sys_script_include.script:
var AdminTools_TableInspector = Class.create();
AdminTools_TableInspector.prototype = {
    initialize: function (tableName_string) {
        this.tableName_string = tableName_string;
        // Check if table is valid
        if (!gs.tableExists(tableName_string)) {
            throw new Error("Invalid Table provided. Must provide a table that exists");
        }
    },

    getFieldsForTable: function () {
        var sdict = new GlideRecord('sys_dictionary');
        sdict.addQuery('name', this.tableName_string);
        sdict.addQuery('internal_type', '!=', 'collection').addOrCondition('internal_type', 'NULL');
        sdict.addQuery('internal_type', '!=', 'GUID').addOrCondition('internal_type', 'NULL');
        sdict.query();
        var fields = [];
        while (sdict.next()) {
            fields.push(sdict.element.toString());
        }
        return fields;
    },

    getEmptyColumns: function () {
        var table = this.tableName_string;
        var emptyColumns = [];
        var fields = this.getFieldsForTable();
        fields.forEach(function (field){
            var rec = new GlideRecord(table);
            rec.addNotNullQuery(field);
            rec.setLimit(1);
            rec.query();

            if (!rec.hasNext()) {
                emptyColumns.push(field);
            }
        });
        return emptyColumns;
    },

    type: 'AdminTools_TableInspector'
};
