sys_ui_page.client_script:
jQuery("#submit").click(function () {

    // Get data from Script Include AdminTools_TableInspector

    var ajaxCall = new GlideAjax('admintools_ajaxinterfaces');
    ajaxCall.addParam('sysparm_scope', "x_25273_admintools");
    ajaxCall.addParam('sysparm_name', 'ajax_getEmptyColumnsForTable');
    ajaxCall.addParam('sysparm_tablename', jQuery("#tablename").val());
    ajaxCall.getXMLAnswer(ajaxCallback);


});

function ajaxCallback(answerFromServer) {

    try {
        var results = JSON.parse(answerFromServer);
        if (!results.error) {
            //---------------------
            // Create Table

            jQuery('#results').DataTable({
                "searching": false,
                "paging": false,
                "autoWidth": true,
                "scrollY": 400,
                "scrollCollapse": true,
                "data": results.data,
                "columns": [{
                    "title": "Column Name",
                    "width": "100%"
                }],
                "dom": 'Bfrtip',
                "buttons": [
                    {
                        extend: 'excel',
                        filename: results.filename
                    },
                    {
                        extend: 'pdf',
                        filename: results.filename
                    }
                ]
            })
        } else {
            jQuery("#errorbox").text(ex);
        }
        //---------------------

    } catch (ex) {
        jQuery("#errorbox").text(ex);
    }
}
